// Loop through all input items
for (const item of $input.all()) {
  const timeString = item.json.time || "";

  // Match common time units like hour/hours/day/days
  const match = timeString.match(/\b(hour|hours|day|days)\b/i);

  // Add only the metric to JSON
  item.json.time_metric = match ? match[1].toLowerCase() : null;
}

return $input.all();

